import React from "react";
import PropTypes from "prop-types";
import { Link, useHistory } from "react-router-dom";
import { deleteDeck } from "../../utils/api";

function ListDecks({ id, name, description, cards, updateCount }) {

  async function handleDeleteClick() {
    const confirmDelete = window.confirm(
      `Delete this deck? \n\nYou will not be able to recover it.`
    );

    if (confirmDelete) {
        await deleteDeck(id);
        updateCount(-1);
    }
  }

  return (
      <div className="card" key={id}>
          <div className="card-body">
              <h3 className="card-title">{name}</h3>
              <p className="card-text">{description}</p>
          </div>
          <div className="d-flex mb-2">
            <Link type="button" className="btn btn-secondary ml-2" to="#">View</Link>
            <Link type="button" className="btn btn-primary mx-2" to="#">Study</Link>
            <button type="button" className="btn btn-danger ml-auto mr-2" to="#"><i className="bi bi-trash-fill"></i>Delete</button>
          </div>
      </div>
  );
}

ListDecks.propTypes = {
  id: PropTypes.number,
  name: PropTypes.string,
  description: PropTypes.string,
  cards: PropTypes.array,
  updateCount: PropTypes.func,
};

export default ListDecks;
