import React, { Fragment, useEffect, useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { readCard, readDeck } from "../../utils/api";

function CardForm() {
  const { deckId, cardId } = useParams();
  const [card, setCard] = useState({f});
  const [deck, setDeck] = useState({});
  const history = useHistory();

  useEffect(() => {
    const abortController = new AbortController();

    //Get deck on mount
    async function getDeck() {
      try {
        const response = await readDeck(deckId, abortController.signal);
        await setDeck({ ...response });
      } catch (err) {
        if (err.name === "AbortError") {
          console.log("Aborted getDeck() in CardForm");
        } else {
          throw err;
        }
      }
    }

    //Get card on mount if cardId present
    async function getCard() {
      try {
        const response = await readCard(deckId, abortController.signal);
        await setCard({ ...response });
      } catch (err) {
        if (err.name === "AbortError") {
          console.log("Aborted getCard() in CardForm");
        } else {
          throw err;
        }
      }
    }

    getDeck();

    if (cardId) {
      getCard();
    }

    return () => abortController.abort();
  }, []);

  //Handle text change
  const handleChange = (event) => {

  }

  //Done/cancel submit handler
  function handleDoneClick() {
    history.push(`/decks/${deckId}`);
  }

  //Form submit handler
  async function handleSubmitClick() {}

  if (deck.id) {
    //edit card
    if (cardId) {
    }
    //new card
    else {
      return (
        <Fragment>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <Link to="/">
                  <i className="bi bi-door-open-fill"></i> Home
                </Link>
              </li>
              <li className="breadcrumb-item">
                <Link to={`/decks/${deckId}`}>
                  <i className="bi bi-door-open-fill"></i> {deck.name}
                </Link>
              </li>
              <li className="breadcrumb-item active">Add Card</li>
            </ol>
          </nav>
          <h2>{deck.name}: Add Card</h2>
          <form>
            <div className="form-group">
              <label htmlFor="frontInput">Front</label>
              <textarea
                className="form-control"
                id="frontInput"
                placeholder="Front side of card"
                value={card.front}
              ></textarea>
            </div>
            <div className="form-group">
              <label htmlFor="backInput">Back</label>
              <textarea
                className="form-control"
                id="backInput"
                placeholder="Back side of card"
              ></textarea>
            </div>
            <div className="d-flex">
              <button type="button" className="btn btn-secondary mr-2" onClick={() => handleDoneClick()}>
                Done
              </button>
              <button type="submit" className="btn btn-primary">
                Save
              </button>
            </div>
          </form>
        </Fragment>
      );
    }
  } else {
    <p>
      <i className="bi bi-hourglass-split"></i>Fetching data...
    </p>;
  }

  return <p>test</p>;
}

export default CardForm;
