import React, { Fragment, useEffect, useState } from "react";
import PropTypes from "prop-types";
import { Link, useHistory, useParams } from "react-router-dom";
import { deleteDeck, listDecks, readDeck } from "../../utils/api";

import ListCards from "./ListCards";

function ViewDeck({ updateCount }) {
  const { deckId } = useParams();
  const [deck, setDeck] = useState({});
  const history = useHistory();

  //Get deck on mount
  useEffect(() => {
    const abortController = new AbortController();

    async function getDeck() {
      try {
        const response = await readDeck(deckId, abortController.signal);
        setDeck({ ...response });
      } catch (err) {
        if (err.name === "AbortError") {
          console.log("Aborted getDeck() in ViewDeck");
        } else {
          throw err;
        }
      }
    }

    getDeck();

    return () => abortController.abort();
  }, []);

  //Delete deck handler
  async function handleDeleteClick() {
    const confirmDelete = window.confirm(
      `Delete this deck? \n\nYou will not be able to recover it.`
    );

    if (confirmDelete) {
      await deleteDeck(deckId);
      updateCount(-1);
      history.push("/");
    }
  }

  //Await deck info
  if (deck) {
    return (
      <Fragment>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">
                <i class="bi bi-door-open-fill"></i> Home
              </Link>
            </li>
            <li className="breadcrumb-item active">{deck.name}</li>
          </ol>
        </nav>
        <div>
          <h3>{deck.name}</h3>
          <p>{deck.description}</p>
        </div>
        <div className="d-flex mb-3">
          <Link to="#" type="button" className="btn btn-secondary">
            <i class="bi bi-pencil-fill"></i> Edit
          </Link>
          <Link type="button" className="btn btn-primary mx-2" to="#">
            <i className="bi bi-journal-check"></i> Study
          </Link>
          <Link type="button" className="btn btn-primary" to="#">
            <i class="bi bi-plus"></i> Add Cards
          </Link>
          <button
            type="button"
            className="btn btn-danger ml-auto mr-2"
            onClick={() => handleDeleteClick()}
          >
            <i className="bi-trash" role="img"></i>
          </button>
        </div>
        <div>
          <h2>Cards</h2>
          {deck.cards.map(({ id, front, back, deckId }) => {
            return (
              <ListCards
                key={id}
                id={id}
                front={front}
                back={back}
                deckId={deckId}
              />
            );
          })}
        </div>
      </Fragment>
    );
  } else {
    return (
      <p>
        <i class="bi bi-hourglass-split"></i>Fetching data...
      </p>
    );
  }
}

ViewDeck.propTypes = {
  updateCount: PropTypes.func,
};

export default ViewDeck;
