import React, { Fragment, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { readDeck } from "../../utils/api";

function Study() {
  const { deckId } = useParams();
  const [deck, setDeck] = useState({});
  const [studyState, setStudyState] = useState({ isFlipped: false, index: 0 });

  useEffect(() => {
    const abortController = new AbortController();

    async function getDeck() {
      try {
        const response = await readDeck(deckId, abortController.signal);
        await setDeck({ ...response });
      } catch (err) {
        if (err.name === "AbortError") {
          console.log("Aborted getDeck() in CardForm");
        } else {
          throw err;
        }
      }
    }

    getDeck();

    return () => abortController.abort();
  }, []);

  function NotEnoughCards() {
    return (
      <Fragment>
        <h3>Not enough cards.</h3>
        <p>
          You need at least 3 cards to study. There are {deck.cards.length}{" "}
          cards in this deck.
        </p>
        <Link
          type="button"
          className="btn btn-primary"
          to={`/decks/${deckId}/cards/new`}
        >
          <i className="bi bi-plus"></i> Add Cards
        </Link>
      </Fragment>
    );
  }

  if (deck.id) {
    return (
      <Fragment>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/">
                <i className="bi bi-house-door-fill"></i> Home
              </Link>
            </li>
            <li className="breadcrumb-item">
              <Link to={`/decks/${deckId}`}>{deck.name}</Link>
            </li>
            <li className="breadcrumb-item active">Study</li>
          </ol>
        </nav>
        <h2>Study: {deck.name}</h2>
        {deck.cards.length < 3 ? <NotEnoughCards /> : "Text"}
      </Fragment>
    );
  } else {
    return (
      <p>
        <i className="bi bi-hourglass-split"></i>Fetching data...
      </p>
    );
  }
}

export default Study;
