import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { useParams } from "react-router-dom";
import { listCards } from "../../utils/api";

function StudyCard({ studyState, setStudyState }) {
  const { deckId } = useParams();
  const [cards, setCards] = useState([]);
  const [currentCard, setCurrentCard] = useState({});

  useEffect(() => {
    const abortController = new AbortController();

    async function getCards() {
      try {
        const response = await listCards(deckId, abortController.signal);
        setCards(response);
      } catch (err) {
        if (err.name === "AbortError") {
          console.log("Aborted getCards() in StudyCard");
        } else {
          throw err;
        }
      }
    }

    getCards();

    return () => abortController.abort();
  }, []);

  if (cards) {
    return (
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">
            Card {studyState.index + 1} of {cards.length}
          </h5>
        </div>
      </div>
    );
  } else {
    return (
        <p>
          <i className="bi bi-hourglass-split"></i>Fetching data...
        </p>
      );
  }
}

StudyCard.propTypes = {
  studyState: PropTypes.object,
  setStudyState: PropTypes.func,
};

export default StudyCard;
